mapper.select:
select a.article_id as articleId,a.article_user_id as articleUserId,a.article_title as articleTitle, a.article_content as articleContent,a.article_view_count as articleViewCount, a.article_comment_count as articleCommentCount, a.article_like_count as articleLikeCount,a.article_is_comment as articleIsComment,a.article_status as articleStatus, a.article_order as articleOrder, date_format(a.article_update_time, '%Y-%m-%d %H:%i:%s') as articleUpdateTime, date_format(a.article_create_time, '%Y-%m-%d %H:%i:%s') as articleCreateTime, a.article_summary as articleSummary, u.user_name as userName from article a join tbl_user u on a.article_user_id=u.user_id where 1=1
AND article_id = #{articleId}
order by articleUpdateTime desc
