mapper.select:
select p.id,p.memo_id,date_format(p.memo_start_time, '%Y-%m-%d') as memo_start_time,date_format(p.memo_end_time, '%Y-%m-%d') as memo_end_time,p.priority,p.matter,p.memo_content,p.input_persion,date_format(p.input_time, '%Y-%m-%d') as input_time ,
p.status
from tbl_memo p
where p.status = 0

    AND p.memo_start_time >= #{memo_start_time}


    AND p.memo_end_time  <= #{memo_end_time}


    AND p.input_time =#{input_time}


    AND p.matter =#{matter}

ORDER BY p.memo_start_time ASC,p.PRIORITY DESC
